import React from 'react';
import UserApi from '../../api/UserApi';
import '../Components.css';

class ProfilePage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      name: '',
      email: '',
      password: '',
    };
  }

  componentDidMount() {
    window.setTimeout(() => {
      this.setState({
        name: this.props.user.name,
        email: this.props.user.email,
        password: '',
      });
    }, 400);
  }

  handleSubmit() {
    const updatedUser = { ...this.props.user };
    updatedUser.name = this.state.name;
    updatedUser.password = this.state.password.length
      ? this.state.password
      : this.props.user.password;
    UserApi.updateUser(updatedUser).then((res) => {
      this.setState({
        name: res.data.name,
        email: res.data.email,
        password: '',
      });
      window.location.reload();
    });
  }

  render() {
    return (
      <div className="container w-50">
        <div className="card rounded-0  ">
          <div className="card-body" style={{ borderRadius: '0px' }}>
            <label className="font-weight-bold">Username</label>
            <div className="input-group">
              <input
                type="text"
                placeholder="Enter your name"
                className="form-control rounded-0"
                style={{ borderRadius: '10px' }}
                value={this.state.name}
                onChange={(e) => this.setState({ name: e.target.value })}
              />
            </div>
          </div>

          <div className="card-body" style={{ borderRadius: '0px' }}>
            <label className="font-weight-bold">Email</label>
            <div className="input-group">
              <input
                disabled
                type="text"
                placeholder="What is your e-mail?"
                className="form-control rounded-0"
                style={{ borderRadius: '10px' }}
                value={this.state.email}
                onChange={(e) => this.setState({ email: e.target.value })}
              />
            </div>
          </div>

          <div className="card-body" style={{ borderRadius: '0px' }}>
            <label className="font-weight-bold">Password</label>
            <div className="input-group">
              <input
                type="password"
                placeholder="Password"
                className="form-control rounded-0 "
                style={{ borderRadius: '10px' }}
                value={this.state.password}
                onChange={(e) => this.setState({ password: e.target.value })}
              />
            </div>
          </div>
          <div className="card-body">
            <button
              className="btn btn-primary btn-sm rounded-0 border-0"
              onClick={(e) => this.handleSubmit()}
              style={{
                backgroundColor: '#0C2C54',
              }}
            >
              Save
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default ProfilePage;
